import { useState } from "react";
import Input from "../components/Input.jsx";
import Button from "../components/Button.jsx";

function SignUp() {
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');

    async function LogIn(e) {
        e.preventDefault();

        const Data = {
            email,
            password
        }

        try {
            const response = await fetch("http://localhost:5100/bread_network/login", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(Data)
            })
            const { token } = await res.json(); localStorage.setItem('token', token); // o sessionStorage

        if(!response.ok) throw new Error('Network response was not ok');

        const result = await response.json();
        console.log('User loged:', result);
        alert('User loged successfully!');  

        } catch (error) {
            console.error('Error:', error);
        }

        }

    return (
        <div className="main-div">
        <div className="login-div">
            <h1>Log In Page</h1>
            <form 
                className="login-form"
                onSubmit={LogIn}
                >
            <Input
                type="email"
                value={email}
                onChange={e => setEmail(e.target.value)}
                placeholder="you@example.com"
                required
            />
            <Input
                type="password"
                value={password}
                onChange={e => setPassword(e.target.value)}
                placeholder="••••••••"
                required
            />
            <Button
                type={"submit"}
                text={"Log In"}
            />
            </form>
        </div>
        </div>
    );
    }

export default SignUp;
